import React from 'react';
import axios from 'axios';
import './Noticias.css';
export default class fetchNoticias extends React.Component{
    state = {
        loading: true,
        Noticias: []
    };

    async componentDidMount(){
        const url = 'http://localhost:8000/api/Noticias/?format=json';
        const response = await axios.get(url);
        const data = await response.data;

        this.setState({Noticias: data, loading: false});
    }

    render() { 
        if (this.state.loading === true) {
            return (
                <div className="card-container">
                    <p>Loading...</p>
                </div>
            );
        }
        else if (!this.state.Noticias.length === null) {
            return (
                <div className="card-container">
                    <p>No se encontraron noticias</p>
                </div>
            );
        }
        else {
            const noticiasJSX=[]
            this.state.Noticias.forEach(noticia => {
                noticiasJSX.push(
                    <div className="col-2">
                        <figure>
                            <p>
                                <img src={noticia.Foto}/>
                            </p>
                        </figure>
                        <div>
                            <div>
                                <p>
                                    <a>
                                        <strong>{noticia.Titulo}</strong>
                                    </a>
                                    <small><i>Equipo: {noticia.Equipo}</i></small>
                                    <br/>
                                    {noticia.Contenido}
                                </p>
                            </div>
                        </div>
                    </div>
                );

            });

            return(
                <div className="container">
                    {noticiasJSX}
                </div>
            )
        }

    }
}
