import React from 'react';
import './Modal.css';

const modal = (item) => {
    return (
        <div>
            <div className="modal-container"
                style={{
                    transform: item.show ? 'translateY(0vh)' : 'translateY(-100vh)',
                    opacity: item.show ? '1' : '0'  
                }}>
                <div className="modal-header">
                    <h3>header</h3>
                    <span className="cerrar-modal" onClick={item.close}>x</span>
                </div>
                <div className="modal-body">
                    <p>
                        {item.Contenido}
                    </p>
                </div>
            </div>
        </div>
    );
}
export default modal;
