import React from 'react';
import axios from 'axios';
import './Modal'

export default class fetchNoticias extends React.Component{
    state = {
        loading: true,
        Noticias: []
    };
    componentWillMount(){
        import ('./Noticias.css');    
    }
    async componentDidMount(){
        const url = 'http://localhost:8000/api/Noticias/?format=json';
        const response = await axios.get(url);
        const data = await response.data;

        this.setState({Noticias: data, loading: false});
    }

    render() { 
        if (this.state.loading === true) {
            return (
                <div className="card-container">
                    <p>Loading...</p>
                </div>
            );
        }
        else if (!this.state.Noticias.length === null) {
            return (
                <div className="card-container">
                    <p>No se encontraron noticias</p>
                </div>
            );
        }
        else {
            const noticiasJSX=[]
            this.state.Noticias.forEach(noticia => {
                noticiasJSX.push(

                    <React.Fragment>
                        <modal className="evento" noticia>
                            <p>modal conten</p>
                        </modal>
                        <div className="car-container">
                            <figure>
                                <p>
                                    <img  height="150px" width="300px" src={noticia.Foto}/>
                                </p>
                            </figure>
                            <div>
                                <div className="container">
                                    <p>
                                        <a>
                                            <strong>{noticia.Titulo}</strong>
                                        </a>
                                        <br/>
                                        <small><i>Equipo: {noticia.Equipo}</i></small>
                                        <br/>
                                        <a name="" id="" class="btn btn-success" role="button">Leer m√°s</a>
                                    </p>
                                </div>
                            </div>
                        </div>
                    </React.Fragment>
                );
            });
            return(
                <div className="container">
                    <div className="row">
                        {noticiasJSX}
                    </div>                    
                </div>
            );
        }

    }
}
