import React from "react";
import axios from "axios";

export default class Compraventa extends React.Component {
  state = {
    loading: true,
    data: [],
    equipo: [],
  };

  componentWillMount() {
    import("./CompraVenta.css");
  }

  async componentDidMount() {
    const url = "http://localhost:8000/api/Jugadores/?search=25-04-2021";
    // Await de la funci칩n para esperar a la respuesta asincrona y obtener los datos

    //equipos
    const response = await axios.get(url);
    const data = await response.data;

    this.setState({
      loading: false,
      data: data,
    });

  }

  async buttonHandler(jugador) {
    const token = [
      id = jugador.id,
      jugador = jugador.jugador,
      edad = jugador.edad,
      pais = jugador.pais,
      equipos = jugador.equipos,
      dorsal = jugador.dorsal,
      posicion = jugador.posicion,
      precio = jugador.precio,
      actualizacion = jugador.actualizacion,
      usuario_equipo = '2',
    ];
    console.log(token);
    await axios.put('http://localhost:8000/api/Jugadores/', token);
  }

  render() {
    // Mostrar치 un escenario dependiendo de si estan o no cargados los datos
    if (this.state.loading === true) {
      return (
        <div className="card-container">
          <p>Loading...</p>
        </div>
      );
    } else if (!this.state.data.length === null) {
      return (
        <div className="card-container">
          <p>No se encontraron datos de los jugadores</p>
        </div>
      );
    } else {
      const compraventaJSx = [];
      const equipo = [];
      console.log(equipo);
      this.state.data.forEach((jugador) => {
        compraventaJSx.push(
          <div className="">
            <div className="card-container" key={jugador.jugador}>
              <div className="card-content">
                <button
                  className={
                    this.state.isComprado ?"btn btn-success" : "btn btn-danger"
                  }    
                  onClick={() => {
                    this.buttonHandler(jugador);
                  }}
                >
                  {jugador.jugador}--{jugador.precio}millones --
                  {jugador.equipos}
                </button>
              </div>
            </div>
          </div>
        );
      });
      return (
        <section className="container">
          <div className="row">
            
            <div className="Container">{compraventaJSx}</div>
            
          </div>
        </section>
      );
    }
  }
}
