import React, { useState, useEffect } from "react";
import axios from "axios";
import ReactDOM from "react-dom";

export default class Compraventa extends React.Component {
  state = {
    loading: true,
    data: [],
    isComprado: [],
    cartera: 500,
  };

  componentWillMount() {
    import("./CompraVenta.css");
  }

  async componentDidMount() {
    const url = "http://localhost:8000/api/Jugadores/?search=25-04-2021";
    // Await de la funci칩n para esperar a la respuesta asincrona y obtener los datos

    //equipos
    const response = await axios.get(url);
    const data = await response.data;
    console.log(data);

    this.setState({
      loading: false,
      data: data,
    });
    this.state.data.forEach((any, index) => {
      this.state.isComprado[index] = false;
    });
  }

  buttonHandler(index, precio, equipo, jugador) {
    if (this.state.isComprado[index] === true) {
      this.state.cartera += precio;
      this.state.isComprado[index] = false;
      this.forceUpdate();
      equipo.splice(equipo.indexOf(jugador), 1);
      return 1;
    } else {
      this.state.cartera -= precio;
      this.state.isComprado[index] = true;
      this.forceUpdate();
      return 0;
    }
  }
  render() {
    // Mostrar치 un escenario dependiendo de si estan o no cargados los datos
    if (this.state.loading === true) {
      return (
        <div className="card-container">
          <p>Loading...</p>
        </div>
      );
    } else if (!this.state.data.length === null) {
      return (
        <div className="card-container">
          <p>No se encontraron datos de los jugadores</p>
        </div>
      );
    } else {
      const compraventaJSx = [];
      const equipo = [];
      console.log(equipo);
      this.state.data.forEach((jugador, index) => {
        compraventaJSx.push(
          <div className="card-container" key={jugador.jugador}>
            <div className="card-content">
              <button
                class="btn btn-dark"
                onClick={() => {
                  if(this.buttonHandler(
                    index,
                    jugador.precio,
                    equipo,
                    jugador.jugador
                  ))
                  console.log(this.state.cartera);
                }}
              >
                {jugador.jugador}--{jugador.precio}millones --{jugador.equipos}
              </button>
            </div>
          </div>
        );
      });
      return (
        <div>
          <h3>tienes {this.state.cartera} millones </h3>

          {compraventaJSx}
        </div>
      );
    }
  }
}
