import React from 'react';
import './Modal.css';

export default class modal extends React.Component {
    
    state={
        showModal: false,
    }

    handleModal (){
        this.state.showModal[index] = !this.state.showModal[index]
    }
    render(item){
        return (
            <div>
                <div className="modal-container"
                    style={{
                        transform: this.handleModal() ? 'translateY(0vh)' : 'translateY(-100vh)',
                        opacity: this.handleModal() ? '1' : '0'  
                    }}>
                    <div className="modal-header">
                        <h3>header</h3>
                        <span className="cerrar-modal" onClick={this.handleModal}>x</span>
                    </div>
                    <div className="modal-body">
                        <p>
                            {item.Contenido}
                        </p>
                    </div>
                </div>
            </div>
        );
    }
}
