import React, { useState } from "react";
import axios from "axios";

export default class Compraventa extends React.Component {
  componentWillMount() {
    import("./CompraVenta.css");
  }

  state = {
    loading: true,
    jugador: [],
    precio: [],
    actualizacion: [],
    equipo: [],
    edad: [],
    posicion: [],
  };

  async componentDidMount() {
    var url = "http://localhost:8000/api/Jugadores/?format=json";
    // Await de la funci칩n para esperar a la respuesta asincrona y obtener los datos
    //equipos
    var response = await axios.get(url);
    const equiposData = await response.data;

    //nombres de los jugadores
    response = await axios.get(url);
    const jugadoresData = await response.data;
    //posicion
    response = await axios.get(url);
    const posicionData = await response.data;
    //edad
    response = await axios.get(url);
    const edadData = await response.data;

    //precio
    response = await axios.get(url);
    const precioData = await response.data;


    
    this.setState({
      jugador: jugadoresData,
      precio: precioData,
      edad: edadData,
      equipo: equiposData,
      posicion: posicionData,
      loading: false,
    });
  }

  render() {
    // Mostrar치 un escenario dependiendo de si estan o no cargados los datos
    if (this.state.loading === true) {
      return (
        <div className="card-container">
          <p>Loading...</p>
        </div>
      );
    } else if (!this.state.jugador.length === null) {
      return (
        <div className="card-container">
          <p>No se encontraron datos de los jugadores</p>
        </div>
      );
    } else {
      // Mapeo del array de equipos por cada uno de los equipos
      const compraventaJSx = [];
      for (let index = 0; index < this.state.jugador.length; index++) {
        compraventaJSx.push(
          <div className='card-container' key={this.state.jugador}>
            <div className='card-content'>
                <h3>{this.state.jugador[index].jugador}</h3>
                <h3>{this.state.equipo[index].equipo}</h3>
            </div>
          </div>
        );
      }
      
      
     /* <div className='card-container' key={this.state.equipos.Equipo}>
      <div className='image-container'>
          <img alt="foto" src={this.state.escudos[index].Link}></img>
      </div>
      <div className='card-content'>
          <div className='card-title'>
              <h3>{this.state.equipos[index].Equipo}</h3>
          </div>
      </div>
    </div>*/

      return <div className="row">{compraventaJSx}</div>;
    }
  }
}
