import React, { useState } from "react";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import "./Login.css";
import axios from "axios";
import { setUserContext, userContext } from "./user";
global.usuarioLogeado = require('./user');

async function loginUser(credenciales) {
  const url =
    "http://localhost:8000/api/Usuarios/?search=" + credenciales.usuario;
  var response = await axios.get(url);
  var data = response.data;

  if (data.length > 0) {
    data.forEach((element) => {
      if (element.nombre === credenciales.usuario) {
        if (element.password === credenciales.password) {
          global.usuarioLogeado = credenciales.usuario;
          console.log(global.usuarioLogeado)
          window.location.replace("/");
        }
      }
    });
  }
}

export default function Login() {

  const userInfo = React.useContext(userContext);
  const setUserInfo = React.useContext(setUserContext);
  const userIsLoged = <div>{(Object.keys(userInfo).length) ? userInfo.first + " " + userInfo.last : null}</div>
  
  const [usuario, setUsuario] = React.useState({});
  const [password, setPassword] = React.useState({});

  const token ={
    usuario,
    password
  };

  if (userIsLoged != null) {
    return (
      <div className="App container">
        <div className="justify-content-center row">
          <h1 style={{ color: "white" }}>Ya estas logeado</h1>
          <button
            className="btn btn-warning"
            onClick={setUserInfo(null)}
          >
            Logout
          </button>
        </div>
      </div>
    );
  }
  return (
    <div className="App container">
      <div className="justify-content-center row">
        <h1 style={{ color: "white" }}>Inicio de sesion</h1>
      </div>
      <Link to="/Register" className="nav-item nav-link active">
        Registro
      </Link>
      <div className="justify-content-center row">
        <form className="mt-5" onSubmit={handleEnviar}>
          <label>
            <p>Username</p>
            <input type="text" onChange={(e) => setUsuario(e.target.value)} />
          </label>
          <label>
            <p>Password</p>
            <input
              type="password"
              onChange={(e) => setPassword(e.target.value)}
            />
          </label>
          <div>
            <button className="btn btn-success" type="submit">
              Iniciar Sesión
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
