import React, { useState } from 'react';
import axios from 'axios';
import Modal from 'react-modal'
//import modal from './Modal'

export default class fetchNoticias extends React.Component{
    
    state = {
        loading: true,
        Noticias: [],
        isOpen: false
    };

    componentWillMount(){
        import ('./Noticias.css');    
    }
    async componentDidMount(){
        const url = 'http://localhost:8000/api/Noticias/?format=json';
        const response = await axios.get(url);
        const data = await response.data;

        this.setState({Noticias: data, loading: false});
    }
    render() { 
        if (this.state.loading === true) {
            return (
                <div className="card-container">
                    <p>Loading...</p>
                </div>
            );
        }
        else if (!this.state.Noticias.length === null) {
            return (
                <div className="card-container">
                    <p>No se encontraron noticias</p>
                </div>
            );
        }
        else {
            
            const noticiasJSX=[];
            
            this.state.Noticias.forEach(noticia => {
                noticiasJSX.push(
                    <div className="ml-3">

                        {//new modal(noticia)
                        }
                        <div className="car-container">
                            <figure>
                                <p>
                                    <img alt={noticia.foto} height="150px" width="300px" src={noticia.Foto}/>
                                </p>
                            </figure>
                            <div>
                                <div className="container">
                                    <p>
                                        <strong>{noticia.Titulo}</strong>
                                        <br/>
                                        <small><i>Equipo: {noticia.Equipo}</i></small>
                                        <br/>
                                        <button class="btn btn-success" onClick={() => {this.setState({isOpen: true})}} >Leer m√°s</button>
                                    </p>
                                </div>
                            </div>
                        </div>
                    </div>      
                );
            });                
            return(
                <div className="container">
                    <Modal isOpen = {this.state.isOpen}>
                        <h2>Modal tittle</h2>
                        <p>Modal Body</p>
                        <button class="btn btn-danger" onClick={() => {this.setState({isOpen: true})}} >Cerrar</button>
                    </Modal>
                    <div className="row">
                        {noticiasJSX}
                    </div>                    
                </div>
            );
        }

    }
}
