import React from 'react';
import axios from 'axios';
import {Modal, Button} from 'react-bootstrap';

export default class fetchNoticias extends React.Component{
    
    state = {
        loading: true,
        Noticias: [],
        showModal: false
    };

    componentWillMount(){
        import ('./Noticias.css');    
    }
    async componentDidMount(){
        const url = 'http://localhost:8000/api/Noticias/?format=json';
        const response = await axios.get(url);
        const data = await response.data;

        this.setState({Noticias: data, loading: false});
    }
    handleModal(){
        this.setState({showModal: !this.state.showModal});
    }
    render() { 
        if (this.state.loading === true) {
            return (
                <div className="card-container">
                    <p>Loading...</p>
                </div>
            );
        }
        else if (!this.state.Noticias.length === null) {
            return (
                <div className="card-container">
                    <p>No se encontraron noticias</p>
                </div>
            );
        }
        else {
            
            const noticiasJSX=[];
            this.state.Noticias.forEach(noticia => {
                noticiasJSX.push( 
                    <div>
                        <Modal show={this.state.showModal}>
                            <Modal.Header closeButton>
                                Modal Header
                            </Modal.Header>
                            <Modal.body>
                                {noticia.contenido}
                            </Modal.body>
                            <Modal.Footer>
                                <Button onClick={() => noticia.handleModal()}>Volver</Button>
                            </Modal.Footer>
                        </Modal>
                        <div className="car-container">
                            <figure>
                                <p>
                                    <img alt={noticia.foto} height="150px" width="300px" src={noticia.Foto}/>
                                </p>
                            </figure>
                            <div>
                                <div className="container">
                                    <p>
                                        <strong>{noticia.Titulo}</strong>
                                        <br/>
                                        <small><i>Equipo: {noticia.Equipo}</i></small>
                                        <br/>
                                        <button class="btn btn-success" onClick="">Leer m√°s</button>
                                    </p>
                                </div>
                            </div>
                        </div>
                    </div>      
                );
            });
            return(
                <div className="container">
                    <div className="row">
                        {noticiasJSX}
                    </div>                    
                </div>
            );
        }

    }
}
