import React, { useState } from "react";
import axios from "axios";
import ReactDOM from 'react-dom'


export default class Compraventa extends React.Component {
  state = {
    loading: true,
    data: [],
  };

  componentWillMount() {
    import("./CompraVenta.css");
  }

 

  async componentDidMount() {
    const url = "http://localhost:8000/api/Jugadores/?search=25-04-2021";
    // Await de la funci칩n para esperar a la respuesta asincrona y obtener los datos

    //equipos
    const response = await axios.get(url);
    const data = await response.data;
    console.log(data);
    
    this.setState({
      loading: false,
      data: data,
      comprado: false,
    });
  }
 
  render() {
    var cartera = 500;

    function comprarVender(cartera){

      //boton.style.backgroundColor = "yellow"
      cartera=cartera-1;


    }



 /*   function buttonhandler(precio,cartera){
        print(jugador.precio)
        if(this.state.comprado==false){
          cartera-=jugador.precio
          this.state.comprado=true
        }
        else{
          cartera+=jugador.precio
          this.state.comprado=false
        }
       
  
    }
*/

    // Mostrar치 un escenario dependiendo de si estan o no cargados los datos
    if (this.state.loading === true) {
      return (
        <div className="card-container">
          <p>Loading...</p>
        </div>
      );
    } else if (!this.state.data.length === null) {
      return (
        <div className="card-container">
          <p>No se encontraron datos de los jugadores</p>
        </div>
      );
    } else {
        const compraventaJSx = [];


      /*
      
   onClick={() => {
                      this.setState({ isOpen: true });
                      {
                        this.setState({ goleadoresModal: true });
                      }
                    }}      
      
      */



        this.state.data.forEach((jugador) => {
          compraventaJSx.push(
            <div className="card-container" key={jugador.jugador}>
              <div className="card-content">
                <button>
                onClick={() => {
                      this.setState({ comprado: true });
                      cartera=1;
                    }}      



                  {jugador.jugador}--{jugador.precio}millones --{jugador.equipos}
                  
                </button>
                
              </div>
            </div>
          );
        });
        return(
            <div>
                <h3>tienes {cartera} millones </h3>

                {compraventaJSx}
            </div>
        );
    }

  }
}
