import React, { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

export default class Plantilla extends React.Component {
  state = {
    loading: true,
    Equipo: "",
    Jugadores: [],
    Fotos: [],
  };
  componentWillMount() {
    console.log(this.props);
  }
  handleEquipo(string) {
    this.setState({
      Equipo: string,
    });
  }
  async componentDidMount() {
    const { EquipoLiga } = this.props.location.state;
    this.handleEquipo(EquipoLiga);
    var url =
      "http://localhost:8000/api/Jugadores/?search=" + this.state.Equipo;
    var response = await axios.get(url);
    var data = await response.data;
    this.setState({ Jugadores: data, loading: false });

    url = "http://localhost:8000/api/Fotos/";
    response = await axios.get(url);
    data = await response.data;
    this.setState({ Fotos: data });
  }

  render() {
    if (this.state.loading === true) {
      return (
        <div className="card-container">
          <p>Loading...</p>
        </div>
      );
    } else if (!this.state.Jugadores.length === null) {
      return (
        <div className="card-container">
          <p>No se encontraron datos del equipo</p>
        </div>
      );
    } else {
      const jugadoresJSX = [];
      this.state.Jugadores.forEach((jugador) => {
        if (jugador.actualizacion === "25-04-2021") {
          jugadoresJSX.push(
            <Link to="">
              <div className="card-container" key={jugador}>
                <div className="image-container">
                  <img
                    alt="foto"
                    src={() => {
                      this.state.Fotos.forEach((dataFoto) => {
                        if (dataFoto.Nombre === jugador.jugador) {
                          console.log(dataFoto.LinkFoto)
                          return dataFoto.LinkFoto;
                        }
                      });
                    }}
                  />
                </div>
                <div className="card-content">
                  <div className="card-title">
                    <h3>{jugador.Equipo}</h3>
                  </div>
                </div>
              </div>
            </Link>
          );
        }
      });
      return <div className="container">{jugadoresJSX}</div>;
    }
  }
}
