import React from 'react';
import axios from 'axios';
import modal from './Modal'

export default class fetchNoticias extends React.Component{
    
    state = {
        loading: true,
        Noticias: [],
        showModal: true
    };

    componentWillMount(){
        import ('./Noticias.css');    
    }
    async componentDidMount(){
        const url = 'http://localhost:8000/api/Noticias/?format=json';
        const response = await axios.get(url);
        const data = await response.data;

        this.setState({Noticias: data, loading: false});
    }
    handleModal = () => {
        this.setState({
            showModal: !this.state.showModal
        });
    }
    render() { 
        if (this.state.loading === true) {
            return (
                <div className="card-container">
                    <p>Loading...</p>
                </div>
            );
        }
        else if (!this.state.Noticias.length === null) {
            return (
                <div className="card-container">
                    <p>No se encontraron noticias</p>
                </div>
            );
        }
        else {
            
            const noticiasJSX=[];

            for (let index = 0; index < this.state.Noticias.length; index++) {
                noticiasJSX.push(
                    <div className="ml-3">
                    <modal
                        className="modal"
                        show={this.state.showModal}
                        close={this.handleModal}>
                            
                    </modal>
                        <div className="car-container">
                            <figure>
                                <p>
                                    <img alt={this.state.Noticias[index].foto} height="150px" width="300px" src={this.state.Noticias[index].Foto}/>
                                </p>
                            </figure>
                            <div>
                                <div className="container">
                                    <p>
                                        <strong>{this.state.Noticias[index].Titulo}</strong>
                                        <br/>
                                        <small><i>Equipo: {this.state.Noticias[index].Equipo}</i></small>
                                        <br/>
                                        <button class="btn btn-success" onClick="" >Leer m√°s</button>
                                    </p>
                                </div>
                            </div>
                        </div>
                    </div>      
                );
            }
            return(
                <div className="container">
                    <div className="row">
                        {noticiasJSX}
                    </div>                    
                </div>
            );
        }

    }
}
