import React from 'react';
import axios from 'axios';
import {Modal, Button} from 'react-bootstrap';

export default class fetchNoticias extends React.Component{
    
    state = {
        loading: true,
        Noticias: [],
        showModal: false
    };
    handleModal(){
        this.setState({showModal: !this.state.showModal});
    }
    componentWillMount(){
        import ('./Noticias.css');    
    }
    async componentDidMount(){
        const url = 'http://localhost:8000/api/Noticias/?format=json';
        const response = await axios.get(url);
        const data = await response.data;

        this.setState({Noticias: data, loading: false});
    }
    
    render() { 
        if (this.state.loading === true) {
            return (
                <div className="card-container">
                    <p>Loading...</p>
                </div>
            );
        }
        else if (!this.state.Noticias.length === null) {
            return (
                <div className="card-container">
                    <p>No se encontraron noticias</p>
                </div>
            );
        }
        else {
            
            const noticiasJSX=[]
            this.state.Noticias.forEach(noticia => {
                noticiasJSX.push(       
                    <div className="car-container">
                        <figure>
                            <p>
                                <img alt={noticia.foto} height="150px" width="300px" src={noticia.Foto}/>
                            </p>
                        </figure>
                        <div>
                            <div className="container">
                                <p>
                                    <a>
                                        <strong>{noticia.Titulo}</strong>
                                    </a>
                                    <br/>
                                    <small><i>Equipo: {noticia.Equipo}</i></small>
                                    <br/>
                                    <a class="btn btn-success" onClick={handleModal} role="button">Leer m√°s</a>
                                </p>
                            </div>
                        </div>
                    </div>
                );
            });
            return(
                <div className="container">
                    <Modal show={this.state.showModal}>
                        <Modal.Header closeButton>
                            Modal Header
                        </Modal.Header>
                        <Modal.body>
                            Body del modal
                        </Modal.body>
                        <Modal.Footer>
                            <Button onClick={() => this.handleModal()}>Volver</Button>
                        </Modal.Footer>
                    </Modal>
                    <div className="row">
                        {noticiasJSX}
                    </div>                    
                </div>
            );
        }

    }
}
