import React, { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

export default class Plantilla extends React.Component {
  state = {
    loading: true,
    Equipo: "",
    Jugadores: [],
    FotosGeneral: [],
    Fotos: []
  };
  componentWillMount() {
    import("./Plantilla.css");
  }
  handleEquipo(string) {
    this.setState({
      Equipo: string,
    });
  }
  async componentDidMount() {
    import("./Plantilla.css");
    const { EquipoLiga } = this.props.location.state;
    this.handleEquipo(EquipoLiga);
    var url = "http://localhost:8000/api/Jugadores/?search=" + this.state.Equipo;
    var response = await axios.get(url);
    const dataEquipo = await response.data;
    this.setState({ Jugadores: dataEquipo, loading: false });

    url = "http://localhost:8000/api/Fotos/?search=" + this.state.Equipo;
    response = await axios.get(url);
    const dataFoto = await response.data;
    //this.setState({ FotosGeneral: dataFoto });
    this.state.FotosGeneral.forEach((foto) => {
      this.state.Fotos.push(foto.LinkFoto);
    });
  }

  render() {
    if (this.state.loading === true) {
      return (
        <div className="card-container">
          <p>Loading...</p>
        </div>
      );
    } else if (!this.state.Jugadores.length === null) {
      return (
        <div className="card-container">
          <p>No se encontraron datos del equipo</p>
        </div>
      );
    } else {
      const jugadoresJSX = [];
      this.state.Jugadores.forEach((jugador, index) => {
        if (
          jugador.actualizacion === "25-04-2021" &&
          jugador.equipos === this.state.Equipo
        ) {
          jugadoresJSX.push(
            <div className="container">
              <div className="card-container" key={jugador.id}>
                <div className="image-container">
                  {console.log(this.state.Fotos),
                  console.log(index)}
                  <img src="" />
                </div>
                <div className="card-content">
                  <div className="card-title">
                    <h3>{jugador.jugador}</h3>
                  </div>
                </div>
              </div>
            </div>
          );
        }
      });
      return <div className="container">{jugadoresJSX}</div>;
    }
  }
}
