import React, { useState } from "react";
import axios from "axios";
import Modal from "react-modal";
import { Bar } from "react-chartjs-2"
export default class Estadisticas extends React.Component {
  componentWillMount() {
    import("./Estadisticas.css");
  }
  state = {
    loading: true,
    clasificacion: [],
    goleadores: [],
    asistencias: [],
    encajados: [],
    asistenciasModal: false,
    goleadoresModal: false,
    clasificacionModal: false,
    encajadosModal: false,
  };

  async componentDidMount() {
    var url = "http://localhost:8000/api/Clasificacion/?format=json";
    // Await de la función para esperar a la respuesta asincrona y obtener los datos
    var response = await axios.get(url);
    const equiposData = await response.data;

    url = "http://localhost:8000/api/Pichichi/?format=json";
    response = await axios.get(url);
    const goleadoresData = await response.data;

    url = "http://localhost:8000/api/Asistencias/?format=json";
    response = await axios.get(url);
    const asistenciasData = await response.data;


    url = "http://localhost:8000/api/Goles_encajados/?format=json";
    response = await axios.get(url);
    const encajadosData = await response.data;

    // Cambio del estado de los datos porque llegado aquí ya se han recibido
    this.setState({
      clasificacion: equiposData,
      goleadores: goleadoresData,
      asistencias: asistenciasData,
      encajados: encajadosData,
      loading: false,
    });
  }



  render() {
    if (this.state.loading === true) {
      return (
        <div className="card-container">
          <p>Loading...</p>
        </div>
      );
    } else if (!this.state.clasificacion.length === null) {
      return (
        <div className="card-container">
          <p>No se encontraron estadisticas</p>
        </div>
      );
    } else {
      // Mapeo del array de equipos por cada uno de los equipos

      const clasificacionJSx = [];
      clasificacionJSx.push(
        <div className="ml-3">
          <div className="car-container">
            <figure>
              <p>
                <img src="https://upload.wikimedia.org/wikipedia/commons/thumb/6/6e/LaLiga_Santander_%282%29.svg/1200px-LaLiga_Santander_%282%29.svg.png" height="150px" width="300px" alt="Clasificacion"
                />
              </p>
            </figure>
            <div>
              <div className="container">
                <p>
                  <br />
                  <button class="btn btn-success" onClick={() => { this.setState({ isOpen: true }); this.setState({ clasificacionModal: true }); }}>
                    VER CLASIFICACIÓN
                  </button>
                </p>
              </div>
            </div>
          </div>
        </div>
      );

      const datosClasificacionJSx = [];

      for (let index = 0; index < this.state.clasificacion.length; index++) {
        datosClasificacionJSx.push(
          <div className="container">
            <div>
              <br />
              <h3>
                {this.state.clasificacion[index].Equipo}{" "}
                {this.state.clasificacion[index].Puntos}
              </h3>
            </div>
          </div>
        );
      }

      const GoleadoresJSx = [];

      GoleadoresJSx.push(
        <div className="ml-3">
          <div className="car-container">
            <figure>
              <p>
                <img src="http://media-s3-us-east-1.ceros.com/bwin/images/2019/06/11/c75338297879f30b21fcbf5d40622c34/botaaaa.png" height="150px" width="300px" alt="Goleadores" />
              </p>
            </figure>
            <div>
              <div className="container">
                <p>
                  <br />
                  <button class="btn btn-success" onClick={() => { this.setState({ isOpen: true }); { this.setState({ goleadoresModal: true }); } }}>
                    VER GOLEADORES
                  </button>
                </p>
              </div>
            </div>
          </div>
        </div>
      );
      const datosGoleadoresJSx = [];
      for (let index = 0; index < this.state.goleadores.length; index++) {
        datosGoleadoresJSx.push(
          <div className="container">
            <Bar
              data={
              Labels=['hola']}
              height={400}
              width={600}
            />
            <div>
              <br />

              <h3>
                {this.state.goleadores[index].Nombre}{" "}
                {this.state.goleadores[index].Goles}
              </h3>
            </div>
          </div>
        );
      }
      const AsistenciasJSx = [];
      GoleadoresJSx.push(
        <div className="ml-3">
          <div className="car-container">
            <figure>
              <p>
                <img src="https://i.pinimg.com/originals/29/ba/97/29ba97dea0b08d6361e7f41fa2e8e740.png" height="150px" width="300px" alt="Asistencias" />
              </p>
            </figure>
            <div>
              <div className="container">
                <p>
                  <br />
                  <button class="btn btn-success" onClick={() => { this.setState({ isOpen: true }); { this.setState({ asistenciasModal: true }); } }}>
                    VER MEJORES ASISTENTES
                  </button>
                </p>
              </div>
            </div>
          </div>
        </div>
      );

      const EncajadosJSx = [];
      EncajadosJSx.push(
        <div className="ml-3">
          <div className="car-container">
            <figure>
              <p>
                <img src="https://w7.pngwing.com/pngs/22/372/png-transparent-soccer-player-illustration-goalkeeper-football-sport-goal-football-boot-sports-equipment-jersey.png" height="150px" width="300px" alt="Encajados" />
              </p>
            </figure>
            <div>
              <div className="container">
                <p>
                  <br />
                  <button class="btn btn-success" onClick={() => { this.setState({ isOpen: true }); { this.setState({ encajadosModal: true }); } }}>
                    VER GOLES ENCAJADOS
                  </button>
                </p>
              </div>
            </div>
          </div>
        </div>
      );
      
      const datosEncajadosJSx = [];

      for (let index = 0; index < this.state.encajados.length; index++) {
        datosEncajadosJSx.push(
          <div className="container">
            <div>
              <br />

              <h3>
                {this.state.asistencias[index].Nombre}{" "}
                {this.state.asistencias[index].Goles_encajados}
              </h3>
            </div>
          </div>
        );
      }

      const datosAsistenciasJSx = [];

      for (let index = 0; index < this.state.asistencias.length; index++) {
        datosAsistenciasJSx.push(
          <div className="container">
            <div>
              <br />

              <h3>
                {this.state.asistencias[index].Nombre}{" "}
                {this.state.asistencias[index].Asistencias}
              </h3>
            </div>
          </div>
        );
      }

      if (this.state.goleadoresModal) {
        return (
          <div className="container">
            <Modal isOpen={this.state.isOpen}>
              <div className="container">
                <h2>GOLEADORES</h2>
                <div className="row">{datosGoleadoresJSx}</div>
                <button class="btn btn-danger" onClick={() => { this.setState({ isOpen: false }); this.setState({ goleadoresModal: false }); }}>
                  Cerrar
                </button>
              </div>
            </Modal>
            <div className="row">{clasificacionJSx}{GoleadoresJSx}{AsistenciasJSx}</div>
          </div>
        );
      }

      if (this.state.encajadosModal) {
        return (
          <div className="container">
            <Modal isOpen={this.state.isOpen}>
              <div className="container">
                <h2>GOLES ENCAJADOS</h2>
                <div className="row">{datosEncajadosJSx}</div>
                <button class="btn btn-danger" onClick={() => { this.setState({ isOpen: false }); this.setState({ encajadosModal: false }); }}>
                  Cerrar
                </button>
              </div>
            </Modal>
            <div className="row">{clasificacionJSx}{GoleadoresJSx}{AsistenciasJSx}{EncajadosJSx}</div>
          </div>
        );
      }
      if (this.state.clasificacionModal) {
        return (
          <div className="container">
            <Modal isOpen={this.state.isOpen}>
              <div className="container">
                <h2>CLASIFICACION</h2>

                <div className="row">{datosClasificacionJSx}</div>
                <button class="btn btn-danger" onClick={() => { this.setState({ isOpen: false }); this.setState({ clasificacionModal: false }); }}>
                  Cerrar
                </button>
              </div>
            </Modal>
            <div className="row">{clasificacionJSx}{GoleadoresJSx}{AsistenciasJSx}</div>
          </div>
        );
      }
      if (this.state.asistenciasModal) {
        return (
          <div className="container">
            <Modal isOpen={this.state.isOpen}>
              <div className="container">
                <h2>ASISTENCIAS</h2>
                <div className="row">{datosAsistenciasJSx}</div>
                <button class="btn btn-danger" onClick={() => { this.setState({ isOpen: false }); this.setState({ AsistenciasModal: false }); }}>
                  Cerrar
                   </button>
              </div>
            </Modal>
            <div className="row">{clasificacionJSx}{GoleadoresJSx}{AsistenciasJSx}</div>
          </div>
        );
      } else {
        return (
          <div className="container">
            <div className="row">{clasificacionJSx}{GoleadoresJSx}{AsistenciasJSx}{EncajadosJSx}</div>
          </div>
        );
      }
    }
  }
}
