import React from 'react';
import axios from 'axios';

// Estructura de los datos en React
export default class FetchEquipo extends React.Component{
    state = {
        loading: true,
        equipo: null,
        jugadores: null,
        edadMedia: null,
        valorTotal: null,
        valorMedio: null
    };

    // Inicio de la petición asincrona a la API
    async componentDidMount() {
        const url = "http://localhost:8000/api/Equipos/?format=json";
        // Await de la función para esperar a la respuesta asincrona y obtener los datos
        const response = await axios.get(url);
        const data = await response.data;
        // Cambio del estado de los datos porque llegado aquí ya se han recibido
        for (let index = 0; index < data.length; index++) {
            this.setState({equipo: data[0], loading: false});            
        }
        
    }

    render() {
        return (
            <div>
                
                {// Se muestra cargando si no existe la persona o esta cargando
                this.state.loading || !this.state.equipo ? (
                    <div>Loading...</div>
                ) : (
                    <div>
                        {this.state.equipo.Equipo}
                        {this.state.equipo.Jugadores}
                        {this.state.equipo.EdadMedia}
                        {this.state.equipo.ValorTotal}
                        {this.state.equipo.ValorMedio}
                    </div>
                )}
            </div>
        );
        
    }
}
