    }
  }
  render() {
    // Mostrar치 un escenario dependiendo de si estan o no cargados los datos
    if (this.state.loading === true) {
      return (
        <div className="card-container">
          <p>Loading...</p>
        </div>
      );
    } else if (!this.state.data.length === null) {
      return (
        <div className="card-container">
          <p>No se encontraron datos de los jugadores</p>
        </div>
      );
    } else {
      const compraventaJSx = [];

      this.state.data.forEach((jugador) => {
        compraventaJSx.push(
          <div className="card-container" key={jugador.jugador}>
            <div className="card-content">
              <button
                class="btn btn-danger"
                onClick={() => {
                  console.log("pulsado");

                  if (this.state.comprado == false) {
                    print(cartera);
                    cartera -= jugador.precio;
                    //print(cartera)

                    this.state.comprado = true;
                  } else {
                    cartera += jugador.precio;
                    this.state.comprado = false;
                  }
                }}
              >
                {jugador.jugador}--{jugador.precio}millones --{jugador.equipos}
              </button>
            </div>
          </div>
        );
      });
      return (
        <div>
          <h3>tienes {cartera} millones </h3>

          {compraventaJSx}
        </div>
      );
    }
  }
}
