import React from 'react';
import './Modal.css';

export default class modal extends React.Component {
    
    state={
        prop: null,
        showModal: false,
    }
    super(prop){
        this.prop = prop;
    }
    handleModal = () => {
        this.setState({
            showModal: !this.state.showModal
        })
    }
    render(){
        return (
            <div>
                <div className="modal-container">
                    <div className="modal-header">
                        <h3>header</h3>
                        <span className="cerrar-modal" onClick={this.handleModal}>x</span>
                    </div>
                    <div className="modal-body">
                        <p>
                            {this.prop.Contenido}
                        </p>
                    </div>
                </div>
            </div>
        );
    }
}
