import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./Login.css";
import axios from "axios";
import { setUserContext, userContext } from "./user";
global.usuarioLogeado = require('./user');

async function loginUser(credenciales) {

  const userInfo = React.useContext(userContext);
  const setUserInfo = React.useContext(setUserContext);
  const userIsLoged =(Object.keys(userInfo).length) ? userInfo.first +" "+ userInfo.last  : null;

  const url = "http://localhost:8000/api/Usuarios/?search=" + credenciales.usuario;
  const response = await axios.get(url);
  const data = await response.data;
  
  if(data[0].nombre === credenciales.usuario){
    if(data[0].password === credenciales.password){
      setUserInfo(data[0].nombre);
      window.location.replace('/');
    }
    else{
      alert('Comprueba los datos introducidos');
    }
  }
  else{
    alert('Comprueba los datos introducidos');
  }
}

export default function Login() {
  
  const [usuario, setUsuario] = React.useState({});
  const [password, setPassword] = React.useState({});

  const handleLogin = async (e) => {
    e.preventDefault();
    await loginUser({
      usuario,
      password,
    });
  };

  if (userIsLoged != null) {
    return (
      <div className="App container">
        <div className="justify-content-center row">
          <h1 style={{ color: "white" }}>Ya estas logeado</h1>
          <button
            type='button'
            className="btn btn-warning"
            onClick={setUserInfo(null)}
          >
            Logout
          </button>
        </div>
      </div>
    );
  }
  return (
    <div className="App container">
      <div className="justify-content-center row">
        <h1 style={{ color: "white" }}>Inicio de sesion</h1>
      </div>
      <Link to="/Register" className="nav-item nav-link active">
        Registro
      </Link>
      <div className="justify-content-center row">
        <form className="mt-5" onSubmit={handleLogin}>
          <label>
            <p>Username</p>
            <input type="text" onChange={(e) => setUsuario(e.target.value)} />
          </label>
          <label>
            <p>Password</p>
            <input
              type="password"
              onChange={(e) => setPassword(e.target.value)}
            />
          </label>
          <div>
            <button type='button' className="btn btn-success" type="submit">
              Iniciar Sesión
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
