import React, { useState } from "react";
import PropTypes from 'prop-types';
import './Login.css';
import axios from "axios";

//
async function loginUser(credenciales) { 
    
    const url = 'http://localhost:8000/api/Usuarios/?search=' + credenciales.usuario;
    var response = await axios.get(url);
    var data = response.data;

    if(data.length > 0){
      data.forEach(element => {
        if(element.nombre  === credenciales.usuario){
          if(element.password === credenciales.password){
            return data;
          }
        }
      });
    }
    return (
      <div className="App container">
        <div className="justify-content-center row">
          <h1 style={{color: 'white'}}>Inicio de sesion</h1>
        </div>
        <div className="justify-content-center row">
          <div class="alert alert-danger alert-dismissible fade show">
              <strong>Error!</strong> A problem has been occurred while submitting your data.
              <button type="button" class="close" data-dismiss="alert">&times;</button>
          </div>
          <form className="mt-5" onSubmit={handleEnviar}>
            <label>
              <p>Username</p>
              <input type="text" onChange={e => setUsuario(e.target.value)}/>
            </label>
            <label>
              <p>Password</p>
              <input type="password" onChange={e => setContrasena(e.target.value)}/>
            </label>
            <div>
              <button className="btn btn-success" type="submit">
                Iniciar Sesión
              </button>
            </div>
          </form>
        </div>
      </div>
    );
}

export default  function Login({ setToken }) {

    const [usuario, setUsuario] = useState();
    const [contrasena, setContrasena] = useState();

    const handleEnviar = async e => {
        e.preventDefault();
        const token = await loginUser({
            usuario, 
            password
        });
        console.log(token)
        setToken(token)
    }

  return (
    <div className="App container">
      <div className="justify-content-center row">
        <h1 style={{color: 'white'}}>Inicio de sesion</h1>
      </div>
      <div className="justify-content-center row">
        <form className="mt-5" onSubmit={handleEnviar}>
          <label>
            <p>Username</p>
            <input type="text" onChange={e => setUsuario(e.target.value)}/>
          </label>
          <label>
            <p>Password</p>
            <input type="password" onChange={e => setContrasena(e.target.value)}/>
          </label>
          <div>
            <button className="btn btn-success" type="submit">
              Iniciar Sesión
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

Login.propTypes = {
  setToken: PropTypes.func.isRequired,
};
